import React,{ useEffect, useState } from 'react';
import axios from 'axios'
import {MDBTable,MDBTableHead,MDBTableBody,MDBRow,MDBCol,MDBContainer, MDBBtnGroup, MDBBtn,MDBInputGroup} from 'mdb-react-ui-kit'

const Read = () => {
    const [data,setdata] = useState([]);
    const [value,setValue] = useState()

  useEffect(()=>{
    userData();

  },[])

  const userData = async()=>{
  return await  axios.get("http://localhost:4000/users")
    .then((res)=>setdata(res.data))
    .catch((err)=>console.log(err))
  }
  console.log("data", data)

  const handleSearch = async (e)=>{
   e.preventDefault();
   return await  axios.get(`http://localhost:4000/users/${value}`)
    .then((res)=>{
        setdata(res.data)
        setValue("")
    })
    .catch((err)=>console.log(err))
  }

  const handleReset = ()=>{

  }
  return (
    <>
       <MDBContainer>
       <MDBInputGroup tag="form" className='d-flex w-auto mb-3'>
              <input className='form-control' placeholder="Search Name.." aria-label="Search" type='Search' />
              <MDBBtn color='info'>Search</MDBBtn>
              <MDBBtn color='danger'>Reset</MDBBtn>
            </MDBInputGroup>

        <div style={{marginTop:"10px"}}>
        <h2>Search,Filter sort ,pagination using json fakr api</h2>
        <MDBRow>
            <MDBCol size="12">
                <MDBTable>
                    <MDBTableHead dark>
                        <tr>
                            <th scope='col'>Sr.No.</th>
                            <th scope='col'>Name</th>
                            <th scope='col'>Email</th>
                            <th scope='col'>Phone</th>
                            <th scope='col'>Address</th>
                            <th scope='col'>Status</th>
                        
                        </tr>
                    </MDBTableHead>
                    {data.length === 0 ? (
                        <MDBTableBody className='align-center mb-0'>
                            tr
                            <tr>
                                <td colSpan={8} className='text-center mb-0'>No Data Found</td>
                            </tr>
                        </MDBTableBody>
                     ):(
                        data.map((item,index)=>(
                            <MDBTableBody style={{ backgroundColor: 'pink' }}>
                                <tr>
                                    <th scope='row'>{index+1}</th>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.mobile}</td>
                                    <td>{item.address}</td>
                                    <td>{item.status}</td>
                                </tr>
                            </MDBTableBody>
                        ))
                     )}
                </MDBTable>
            </MDBCol>
        </MDBRow>
             
        </div>
       </MDBContainer>
    </>
  )
}

export default Read
