import React, { useState } from "react";
import { MDBInput, MDBBtn, MDBContainer, MDBCol, MDBRow } from "mdb-react-ui-kit";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Create = () => {
  const navigate = useNavigate()
  const [user,setUser] = useState({name:"",email:"",mobile:"",address:"",status:""});
  const handleChange = (e)=>{
    setUser({...user,[e.target.name]:e.target.value})

  }

  const handleSubmit = async (e)=>{
         e.preventDefault()
         try{
          if(!user.name || !user.email || !user.mobile || !user.address || !user.status)
          {
            navigate('/submit')
          }
          else
          {
            await axios.post(`http://localhost:4000/users`,
            {
              name:user.name,
              email:user.email,
              mobile:user.mobile,
              address:user.address,
              status:user.status
            })
          }
         }
         catch
         {
          console.log("Something is Went Wrong")
         }
         setUser({name:"",email:"",mobile:"",address:"",status:""})
  }
  return (
    <>
      <MDBContainer>
        <form className="pt-4" onSubmit={handleSubmit}>
       
        <MDBRow size='12'>
        <MDBCol size='3'></MDBCol>
        <MDBCol size='6'>
        <h1 style={{backgroundColor:"red",opacity:".5"}}>What is Your Name ?</h1>
        <MDBInput name="name" type="text" wrapperClass="mb-4" label="Name" onChange={handleChange} />
        </MDBCol>
        </MDBRow>
       

        
          <MDBInput name="email" type="email" wrapperClass="mb-4" label="Email address" onChange={handleChange} />
          <MDBInput name="mobile" type="tel" wrapperClass="mb-4" label="Phone" onChange={handleChange} />
          <MDBInput name="address" type="text" wrapperClass="mb-4" label="Address" onChange={handleChange} />
          <MDBInput name="status" type="text" wrapperClass="mb-4" label="Status" onChange={handleChange} />
          <MDBBtn type="submit" className="mb-4" block>
            Add User
          </MDBBtn>
        </form>
      </MDBContainer>
    </>
  );
};

export default Create;
