import React,{ useEffect, useState } from 'react';
import axios from 'axios'
import {MDBTable,MDBTableHead,MDBTableBody,MDBRow,MDBCol,MDBContainer, MDBBtn, MDBBtnGroup} from 'mdb-react-ui-kit'

const Read = () => {
    const [data,setdata] = useState([]);
    const [value,setValue] = useState();
    const [sortvalue,setSortValue] = useState()
    const sortOptions = ["name","address","email","mobile","state"]

  useEffect(()=>{
    userData();

  },[])

  const userData = async()=>{
  return await  axios.get("http://localhost:4000/users")
    .then((res)=>setdata(res.data))
    .catch((err)=>console.log(err))
  }
  console.log("data", data)

  const handleSearch = async (e)=>{
   e.preventDefault();
   return await  axios.get(`http://localhost:4000/users?q=${value}`)
    .then((res)=>{
        setdata(res.data);
        setValue("");
    })
    .catch((err)=>console.log(err))
  }

  const handleReset = async(e)=>{
   userData()
  }
  const handleSort = async(e)=>{
    let value = e.target.value;
    setSortValue(value)
    return await  axios.get(`http://localhost:4000/users?_sort=${value}&_order=asc`)
    .then((res)=>{
        setdata(res.data);
        
    })
    .catch((err)=>console.log(err))
    
  }

  const handleFilter = async()=>{
    return await  axios.get(`http://localhost:4000/users?_sort=${value}&_order=asc`)
    .then((res)=>{
        setdata(res.data);
        
    })
    .catch((err)=>console.log(err))
  }
  return (
    <>
       <MDBContainer>
       <form 
       style={{
        margin:"auto",
        padding:"15px",
        maxWidth:"400px",
        alignContent:"center"
       }} 
        className='d-flex w-auto mb-3'
        onSubmit={handleSearch}>

              <input className='form-control' 
              placeholder="Search Name.." 
              aria-label="Search" type='text' 
                value={value}
                onChange={(e)=>setValue(e.target.value)}
              />
              <MDBBtn color='info' type='submit'>Search</MDBBtn>
              <MDBBtn color='danger' onClick={handleReset}>Reset</MDBBtn>
            </form>

        <div style={{marginTop:"10px"}}>
        <h2>Search,Filter sort ,pagination using json fakr api</h2>
        <MDBRow>
            <MDBCol size="12">
                <MDBTable>
                    <MDBTableHead dark>
                        <tr>
                            <th scope='col'>Sr.No.</th>
                            <th scope='col'>Name</th>
                            <th scope='col'>Email</th>
                            <th scope='col'>Phone</th>
                            <th scope='col'>Address</th>
                            <th scope='col'>Status</th>
                        
                        </tr>
                    </MDBTableHead>
                    {data.length === 0 ? (
                        <MDBTableBody className='align-center mb-0'>
                            tr
                            <tr>
                                <td colSpan={8} className='text-center mb-0'>No Data Found</td>
                            </tr>
                        </MDBTableBody>
                     ):(
                        data.map((item,index)=>(
                            <MDBTableBody style={{ backgroundColor: 'pink' }}>
                                <tr>
                                    <th scope='row'>{index+1}</th>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.mobile}</td>
                                    <td>{item.address}</td>
                                    <td>{item.status}</td>
                                </tr>
                            </MDBTableBody>
                        ))
                     )}
                </MDBTable>
            </MDBCol>
        </MDBRow>
             
        </div>
        <MDBRow>
            <MDBCol size="8">
                <h5>Sort by :</h5>
                <select style={{width:"50%",borderRadius:"2px",height:"35px"}}
                onChange={handleSort}
                value={sortvalue}>
                <option>Please Select Value</option>
               {sortOptions.map((item,index)=>(
                <option value={item} key={index}>{item}</option>
               ))}

                </select>
            </MDBCol>
            <MDBCol size="4"><h4>Filter by Status</h4>
            <MDBBtnGroup>
                <MDBBtn color='success' onClick={()=>handleFilter("Active")}>Active</MDBBtn>
                <MDBBtn color='warning' onClick={()=>handleFilter("INActive")}>Inactive</MDBBtn>
            </MDBBtnGroup></MDBCol>
        </MDBRow>
       </MDBContainer>
    </>
  )
}

export default Read
